mapper.select:

select * from (select rownum rnum, t1.* from 
(select * from product order by p_code) t1) 
where rnum >= #{start} and rnum <= #{end}


select * from (select rownum rnum, t1.* from 
(select * from productio order by io_regdate desc) t1) 
where rnum >= #{start} and rnum <= #{end}

mapper.update:
update product set p_name = #{p_name}, p_image = #{p_image}, p_image_d = #{p_image_d}, p_image_m = #{p_image_m},
p_cate = #{p_cate}, p_cate_d = #{p_cate_d}, p_price = #{p_price},
p_content = #{p_content}, p_mdate = sysdate where p_code = #{p_code}
